/**
 * Get the appropriate SVG icon for a file
 * @param filename - The filename to get an icon for
 * @param isDirectory - Whether this is a directory
 * @returns SVG string
 */
export function getFileIconSvg(filename: string, isDirectory: boolean = false): string {
  if (isDirectory) {
    return folderIconSvg;
  }

  // Check for special filenames first
  const lowerFilename = filename.toLowerCase();

  // Special file handling
  if (lowerFilename === '.env' || lowerFilename.startsWith('.env.')) {
    return fileIcons['env'] ?? defaultFileIcon;
  }
  if (lowerFilename === '.gitignore') {
    return fileIcons['gitignore'] ?? defaultFileIcon;
  }
  if (lowerFilename === '.dockerignore') {
    return fileIcons['dockerignore'] ?? defaultFileIcon;
  }
  if (lowerFilename === 'dockerfile' || lowerFilename.startsWith('dockerfile.')) {
    return fileIcons['dockerfile'] ?? defaultFileIcon;
  }
  if (lowerFilename === 'makefile' || lowerFilename === 'gnumakefile') {
    return fileIcons['makefile'] ?? defaultFileIcon;
  }

  // Get extension
  const ext = filename.split('.').pop()?.toLowerCase() ?? '';

  return fileIcons[ext] ?? defaultFileIcon;
}

/**
 * Get the mention icon SVG (@ symbol)
 * @returns SVG string for mention icon
 */
export function getMentionIconSvg(): string {
  return mentionIconSvg;
}

// Default symbol icon
const defaultSymbolIcon = `<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16"><path fill="#90a4ae" d="M2 2v12h12V2zm5 9H6V5h2v1H7v4h1v1z"/></svg>`;

/**
 * Get the appropriate SVG icon for a symbol type
 * @param symbolType - The symbol type (function, method, class, struct, interface, type, etc.)
 * @returns SVG string
 */
export function getSymbolIconSvg(symbolType: string): string {
  return symbolIcons[symbolType.toLowerCase()] ?? defaultSymbolIcon;
}
